mapper.select:
SELECT	A.*
FROM 	rfid_appr_file A
WHERE 	RQSTNO = #{rqstno}
		AND FILE_SN = #{fileSn}

mapper.insert:
        INSERT INTO rfid_appr_file (
        		RQSTNO
				,FILE_SN
				,FILE_PATH
				,FILE_NM
				,ORIGNL_FILE_NM
				,FILE_EXTSN
				,USE_YN
				,WEB_FILE_PATH
        )
        SELECT	#{rqstno}
				,(SELECT IFNULL(MAX(FILE_SN),0)+1 FROM rfid_appr_file WHERE RQSTNO = #{rqstno})
				,#{filePath}
				,#{fileNm}
				,#{orignlFileNm}
				,#{fileExtsn}
				,#{useYn}
				,#{webFilePath}
		FROM	DUAL

mapper.update:
        UPDATE 	rfid_appr_file
        SET
				FILE_SN = #{fileSn}
				,FILE_PATH = #{filePath}
				,FILE_NM = #{fileNm}
				,ORIGNL_FILE_NM = #{orignlFileNm}
				,FILE_EXTSN = #{fileExtsn}
				,USE_YN = #{useYn}
				,WEB_FILE_PATH = #{webFilePath}
        WHERE 	RQSTNO = #{rqstno}

        UPDATE 	rfid_appr_file
        SET
				USE_YN = 'N'
        WHERE 	RQSTNO = #{rqstno}
        		AND FILE_SN = #{fileSn}

mapper.delete:
DELETE
FROM 	rfid_appr_file
WHERE 	RQSTNO = #{rqstno}
		AND FILE_SN = #{fileSn}
