mapper.select:
SELECT	A.*
FROM 	RFID_APPR_IO_IN A
WHERE 	IN_RQSTNO = #{inRqstno}

SELECT	A.*
FROM 	RFID_APPR_IO_IN A
WHERE 	RQSTNO = #{rqstno}

mapper.update:
        UPDATE 	RFID_APPR_IO_IN
        SET
				RQSTNO = #{rqstno}
				,IN_DT = #{inDt}
				,IN_REMARK = #{inRemark}
				,FRST_REGIST_PNTTM = #{frstRegistPnttm}
				,FRST_REGISTER_ID = #{frstRegisterId}
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	IN_RQSTNO = #{inRqstno}

        UPDATE 	RFID_APPR_IO_IN
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	IN_RQSTNO = #{inRqstno}
