mapper.sql:
AND B.RQST_STATUS_CD NOT IN ('4')
AND ( B.RQST_USER_NO = #ssUserNo# OR EXISTS ( SELECT 1 FROM RFID_APPR_ASSET T1 INNER JOIN RFID_ASSET T2 ON T1.ASSET_SEQ = T2.ASSET_SEQ WHERE T1.RQSTNO = A.RQSTNO AND T2.USER_NO = #ssUserNo# ) )
AND ( B.RQST_DEPT_NO = #ssDeptNo# OR EXISTS ( SELECT 1 FROM RFID_APPR_ASSET T1 INNER JOIN RFID_ASSET T2 ON T1.ASSET_SEQ = T2.ASSET_SEQ WHERE T1.RQSTNO = A.RQSTNO AND T2.USER_NO = #ssUserNo# ) )
AND 1=1
AND 1=2
AND EXISTS ( SELECT 1 FROM RFID_APPR_ASSET T1 LEFT OUTER JOIN RFID_APPR_IO_IN_ASSET T2 ON T1.RQSTNO = T2.RQSTNO AND T1.ASSET_SEQ = T2.ASSET_SEQ WHERE T2.ASSET_SEQ IS NULL ) AND EXISTS ( SELECT 1 FROM RFID_APPR_ASSET A1 INNER JOIN RFID_ASSET A2 ON A1.ASSET_SEQ = A2.ASSET_SEQ WHERE A1.RQSTNO = A.RQSTNO
AND A2.ASSET_NO LIKE concat('%',#{sAssetNo},'%')
AND A2.ITEM_CD LIKE concat('%',#{sItemCd},'%')
AND A2.ITEM_NAME LIKE concat('%',#{sItemName},'%')
AND A2.ASSET_NAME LIKE concat('%',#{sAssetName},'%')
)
AND B.RQST_USER_NO = #{sRqstUserNo}
AND B.RQST_USER_NAME LIKE concat('%',#{sRqstUserName},'%')
AND B.RQST_REGTIME >= #{sRqstRegtimeS}
AND B.RQST_REGTIME <= #{sRqstRegtimeE}
AND B.RQST_DEPT_NO = #{sRqstDeptNo}
AND B.RQST_DEPT_NAME = #{sRqstDeptName}
AND B.RQST_STATUS_CD = #{sRqstStatusCd}

