mapper.insert:
        INSERT INTO RFID_APPR_RQST (
        		RQSTNO
				,RQST_REGTIME
				,RQST_USER_NO
				,RQST_USER_NAME
				,RQST_DEPT_NO
				,RQST_DEPT_NAME
				,RQST_TOP_USER_NO
				,RQST_TOP_USER_NAME
				,RQST_ASSET_CNT
				,RQST_STATUS_CD
				,FRST_REGIST_PNTTM
				,FRST_REGISTER_ID
				,LAST_UPDT_PNTTM
				,LAST_UPDUSR_ID
        )
        SELECT	#{rqstno}
        		,#{rqstRegtime}
				,#{rqstUserNo}
				,#{rqstUserName}
				,#{rqstDeptNo}
				,#{rqstDeptName}
				,#{rqstTopUserNo}
				,#{rqstTopUserName}
				,COUNT(*) RQST_ASSET_CNT
				,'2' RQST_STATUS_CD
				,#{frstRegistPnttm}
				,#{frstRegisterId}
				,#{lastUpdtPnttm}
				,#{lastUpdusrId}
        FROM	RFID_APPR_ASSET
        WHERE 	RQSTNO = #{rqstno}

mapper.update:
        UPDATE 	RFID_APPR_RQST
        SET
				RQSTNO = #{rqstno}
				,RQST_REGTIME = #{rqstRegtime}
				,RQST_USER_NO = #{rqstUserNo}
				,RQST_USER_NAME = #{rqstUserName}
				,RQST_DEPT_NO = #{rqstDeptNo}
				,RQST_DEPT_NAME = #{rqstDeptName}
				,RQST_TOP_USER_NO = #{rqstTopUserNo}
				,RQST_TOP_USER_NAME = #{rqstTopUserName}
				,RQST_ASSET_CNT = #{rqstAssetCnt}
				,RQST_STATUS_CD = #{rqstStatusCd}
				,FRST_REGIST_PNTTM = #{frstRegistPnttm}
				,FRST_REGISTER_ID = #{frstRegisterId}
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	RQSTNO = #{rqstno}
