mapper.update:
UPDATE 	RFID_ASSET
SET		TOP_ASSET_SEQ = #{topAssetSeq}
		,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
		,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	ASSET_SEQ = #{assetSeq}

UPDATE 	RFID_ASSET
SET		TOP_ASSET_SEQ = null
		,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
		,LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE	ASSET_SEQ = #{assetSeq}
