mapper.update:
UPDATE	rfid_doc
SET		DOCSTATCD = #{docstatcd},
		DOCSTATNM = #{docstatnm},
		REGTIME = #{regtime},
		MODTIME = #{modtime},
		UPDATEYN = 'N',
		PRGSNO = #{prgsno}
WHERE	RQSTNO = #{rqstno}
AND		DOCSTATCD  <>  #{docstatcd}

UPDATE	rfid_doc
SET		UPDATEYN = 'Y'
WHERE	RQSTNO = #{rqstno}

UPDATE  rfid_appr_rqst
SET     RQST_STATUS_CD = #{rqstStatusCd}
WHERE   RQSTNO = #{rqstno}
		AND IFNULL(RQST_STATUS_CD,'999')  <>  #{rqstStatusCd}

UPDATE  rfid_appr_io_ext
SET     RQST_STATUS_CD = #{rqstStatusCd}
WHERE   EXT_RQSTNO = #{rqstno}
		AND IFNULL(RQST_STATUS_CD,'999')  <>  #{rqstStatusCd}

UPDATE  rfid_appr_io_in
SET     RQST_STATUS_CD = #{rqstStatusCd}
WHERE   IN_RQSTNO = #{rqstno}
		AND IFNULL(RQST_STATUS_CD,'999')  <>  #{rqstStatusCd}

UPDATE  rfid_appr_disuse
SET     DISUSE_COMP_DT = #{disuseCompDt}
WHERE   RQSTNO = #{rqstno}

UPDATE  rfid_appr_usercng
SET     AUC_COMP_DT = #{aucCompDt}
WHERE   RQSTNO = #{rqstno}
