mapper.select:
SELECT	A.*
FROM 	RFID_DEVICE_LOG A
WHERE 	DEVICE_LOG_SEQ = #{deviceLogSeq}

mapper.update:
        UPDATE 	RFID_DEVICE_LOG
        SET
				DEVICENO = #{deviceno}
				,FRST_REGIST_PNTTM = #{frstRegistPnttm}
        WHERE 	DEVICE_LOG_SEQ = #{deviceLogSeq}

        UPDATE 	RFID_DEVICE_LOG
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	DEVICE_LOG_SEQ = #{deviceLogSeq}
