        SET
				QTY_ON_HAND = #{qtyOnHand}
				,PRE_QTY_ON_HAND = #{preQtyOnHand}				
				,QTYINVOICED = #{qtyinvoiced}
        WHERE 	PT_OD_ID = #{ptOdId}
        AND SUB_PT_OD_ID = #{subPtOdId}
