mapper.select:
SELECT	A.*
FROM 	rfid_log A
WHERE 	LOG_SEQ = #{logSeq}

mapper.update:
        UPDATE 	rfid_log
        SET
				LOG_TYPE = #{logType}
				,LOG_DT = #{logDt}
				,LOG_CONT = #{logCont}
				,LOG_IP = #{logIp}
        WHERE 	LOG_SEQ = #{logSeq}

        UPDATE 	rfid_log
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	LOG_SEQ = #{logSeq}
