mapper.update:
        UPDATE 	RFID_POSITION
        SET
				POS_NAME = #{posName}
				,POS_LEVEL = #{posLevel}
				,PARENT_POS_NO = #{parentPosNo}
				,USE_YN = #{useYn}
				,FRST_REGIST_PNTTM = #{frstRegistPnttm}
				,FRST_REGISTER_ID = #{frstRegisterId}
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	POS_NO = #{posNo}

        UPDATE 	RFID_POSITION
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	POS_NO = #{posNo}
