mapper.select:
SELECT	A.*
FROM 	RFID_PRINT_HISTORY A
WHERE 	PRINT_SEQ = #{printSeq}

mapper.insert:
        INSERT INTO RFID_PRINT_HISTORY (
        		PRINT_SEQ
				,ASSET_SEQ
				,ASSET_NO
				,RFID_NO
				,ASSET_NAME
				,ASSET_SIZE
				,ASSET_TYPE
				,AQUSIT_DT
				,ORG_NO
				,INV_YEAR
				,INV_NO
				,REQ_SYSTEM
				,PRINT_DT
				,PRINT_TYPE
				,PRINT_YN
				,TMP_TITLE
				,ETC1
				,ETC2
				,ETC3
				,ETC4
        ) VALUES (
				#{printSeq}
				,#{assetSeq}
				,#{assetNo}
				,#{rfidNo}
				,#{assetName}
				,NULL
				,#{assetType}
				,#{aqusitDt}
				,#{orgNo}
				,#{invYear}
				,CASE WHEN #{invNo} = '' THEN NULL ELSE #{invNo} END
				,#{reqSystem}
				,#{printDt}
				,#{printType}
				,#{printYn}
				,#{tmpTitle}
				,#{etc1}
				,#{etc2}
				,#{etc3}
				,#{etc4}
        )

mapper.update:
        UPDATE 	RFID_PRINT_HISTORY
        SET
				ASSET_SEQ = #{assetSeq}
				,ASSET_NO = #{assetNo}
				,RFID_NO = #{rfidNo}
				,ASSET_NAME = #{assetName}
				,ASSET_SIZE = #{assetSize}
				,ASSET_TYPE = #{assetType}
				,AQUSIT_DT = #{aqusitDt}
				,ORG_NO = #{orgNo}
				,INV_YEAR = #{invYear}
				,INV_NO = #{invNo}
				,REQ_SYSTEM = #{reqSystem}
				,PRINT_DT = #{printDt}
				,PRINT_TYPE = #{printType}
				,PRINT_YN = #{printYn}
				,TMP_TITLE = #{tmpTitle}
				,ETC1 = #{etc1}
				,ETC2 = #{etc2}
				,ETC3 = #{etc3}
				,ETC4 = #{etc4}
        WHERE 	PRINT_SEQ = #{printSeq}

        UPDATE 	RFID_PRINT_HISTORY
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	PRINT_SEQ = #{printSeq}
