mapper.insert:
INSERT INTO rfid_print_request (reqst_user_id, org_no, asset_no, label_seq, addr_seq, print_status_cd, use_yn, frst_regist_pnttm, frst_register_id, last_updt_pnttm, last_updusr_id) SELECT #{reqstUserId} reqst_user_id, #{orgNo} org_no, asset_no, #{labelSeq} label_seq, #{addrSeq} addr_seq, #{printStatusCd} print_status_cd, 'Y' use_yn, #{frstRegistPnttm} frst_regist_pnttm, #{frstRegisterId} frst_register_id, #{lastUpdtPnttm} last_updt_pnttm, #{lastUpdusrId} last_updusr_id FROM st$orgNo$_asset ast WHERE ast.asset_no IN (
#{assetNo[]}
)

mapper.select:
select count(*)
from st$orgNo$_asset ast
inner join rfid_print_request pr
		on pr.asset_no = ast.asset_no
		and pr.reqst_user_id = #{reqstUserId}
		and pr.org_no = #{orgNo}
		and pr.print_status_cd = #{printStatusCd}
where pr.use_yn = 'Y'

mapper.update:
update rfid_print_request set 
	use_yn = 'N'
where reqst_user_id = #{reqstUserId}
and org_no = #{orgNo}
and print_status_cd = #{printStatusCd}
			
	and reqst_seq in (
	    				
			#{reqstSeq[]}
			
		)

update rfid_print_request set 
	print_status_cd = 'R',
	label_seq = #{labelSeq},
	addr_seq = #{addrSeq#}
	last_updt_pnttm = #{lastUpdtPnttm},
	last_updusr_id = #{lastUpdusrId} 
where reqst_user_id = #{reqstUserId}
and org_no = #{orgNo}
and print_status_cd = 'T'
