mapper.select:
SELECT 	*
FROM 	rfid_user
WHERE 	user_id = #{userId}
		AND user_pw = #{userPw}

select ifnull(invm.inv_year,' ') inv_year, ifnull(invm.inv_no,'0') inv_no, ifnull(invm.inv_start_dt,' ') inv_start_dt, ( select count(*) from rfid_inventory where inv_year = invm.inv_year and inv_no = invm.inv_no ) total_count, ( select count(*) from rfid_inventory where inv_year = invm.inv_year and inv_no = invm.inv_no and inv_target_yn = 'Y' ) inv_target_count from rfid_inventory_master invm where 1=1
ORDER BY invm.inv_year #{dataOrderArrow}, #{dataOrder} #{dataOrderArrow}
ORDER BY invm.inv_year desc, invm.inv_no desc

