mapper.select:
select ifnull(usr.user_no,' ') user_no, ifnull(usr.user_name,' ') user_name, ifnull(dpt.dept_no,' ') dept_no, ifnull(dpt.dept_name,' ') dept_name, ' ' org_no, ' ' org_name from rfid_user usr inner join rfid_dept dpt on usr.dept_no = dpt.dept_no where 1=1
ORDER BY #{dataOrder} #{dataOrderArrow}
ORDER BY user_name

select ifnull(dpt.dept_no,' ') dept_no, ifnull(dpt.dept_name,' ') dept_name, ' ' org_no, ' ' org_name from rfid_dept dpt where 1=1
ORDER BY #{dataOrder} #{dataOrderArrow}
ORDER BY dept_name

select ifnull(pos.pos_no,' ') pos_no, ifnull(pos.pos_name,' ') pos_name from rfid_position pos where 1=1
ORDER BY #{dataOrder} #{dataOrderArrow}
ORDER BY pos.pos_name

select ifnull(org.org_no,' ') org_no, ifnull(org.org_name,' ') org_name from rfid_org org where 1=1
ORDER BY #{dataOrder} #{dataOrderArrow}
ORDER BY org.org_name

mapper.sql:
    	and af.use_yn = 'Y'
    			
			and af.asset_no = #{assetNo}		
		
					
			and af.file_dt = #{fileDt}

mapper.update:
update rfid_asset_file set
	use_yn = 'N'
where asset_no = #{assetNo}
and file_dt = #{fileDt}

	
	update rfid_inventory set
		cng_asset_size = #{cngAssetSize}
		, cng_org_no = #{cngOrgNo}
		, cng_org_name = #{cngOrgName}
		, cng_dept_no = #{cngDeptNo}
		, cng_dept_name = #{cngDeptName}
		, cng_user_no = #{cngUserNo}
		, cng_user_name = #{cngUserName}
		, cng_pos_no = #{cngPosNo}
		, cng_pos_name = #{cngPosName}
		, cng_hosil = #{cngHosil}
		, cng_remark = #{cngRemark}
		, cng_basic_dt = #{cngBasicDt}
		, tag_read_yn = #{tagReadYn}
		, tag_read_dt = #{tagReadDt}
		, tag_insp_name = #{tagInspName}
		, update_dt = #{updateDt}
	where inv_year = #{invYear}
	and inv_no = #{invNo}
	and asset_no = #{assetNo}
	and (
		update_dt is null
		or
		update_dt < #{updateDt}
		)

mapper.select:
