mapper.select:
SELECT A.* FROM RFID_USER A WHERE 1=1 AND USE_YN = 'Y'
AND USER_NO = #{userNo}
AND EMP_NO = #{empNo}
AND 1=2

SELECT A.*
  FROM RFID_USER A
 WHERE 1=1
 	   AND USE_YN = 'Y'
 	   AND USER_ID = #{userId}
 	   AND USER_PW = #{userPw}

mapper.insert:
        INSERT INTO RFID_USER (
        		USER_NO
        		,USER_ID
        		,USER_PW
				,USER_NAME
				,GRANT_NO
				,USE_YN
				,FRST_REGIST_PNTTM
				,FRST_REGISTER_ID
				,LAST_UPDT_PNTTM
				,LAST_UPDUSR_ID
				,USER_KEY
        )
        SELECT	(SELECT IFNULL(MAX(USER_NO),'10001') + 1 FROM RFID_USER) USER_NO        
				,#{userId}
				,#{userPw}
				,#{userName}
				,#{grantNo}
				,#{useYn}
				,#{frstRegistPnttm}
				,#{frstRegisterId}
				,#{lastUpdtPnttm}
				,#{lastUpdusrId}
				,#{userKey}
		FROM	DUAL

mapper.update:
        UPDATE 	RFID_USER
        SET
				USER_NAME = #{userName}
				 				
				,USER_PW = #{userPw}				
				
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	USER_NO = #{userNo}

        UPDATE 	RFID_USER
        SET
				USE_YN = 'N'
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	USER_NO = #{userNo}

        UPDATE 	RFID_USER
        SET
				GRANT_NO = #{grantNo}
				,LAST_UPDT_PNTTM = #{lastUpdtPnttm}
				,LAST_UPDUSR_ID = #{lastUpdusrId}
        WHERE 	USER_NO = #{userNo}
