mapper.update:
UPDATE	RFID_ASSET
SET		TOP_DEPT_NO = #{topDeptNo}
		,TOP_DEPT_NAME = #{topDeptName}
		,DEPT_NO = #{deptNo}
		,DEPT_NAME = #{deptName}
		,USER_NO = #{userNo}
		,USER_NAME = #{userName}
		,POS_NO = #{posNo}
		,POS_NAME = #{posName}
WHERE	ASSET_SEQ = #{assetSeq}

    	UPDATE	RFID_ASSET
    	SET		ASSET_TYPE_CD = #{assetTypeCd}
				,ITEM_CD = #{itemCd}
				,ITEM_NAME = #{itemName}
    			,ASSET_NAME = #{assetName}
    	WHERE	PURNO = #{purno}
				AND RQSTNO = #{rqstno}
				AND PRODNO = #{prodno}
