SELECT a.EQUIP_ID , a.ASSET_NO , a.KOR_NM , b.EQUIP_NO , b.TAKE_DT , FLOOR(IFNULL((select sum(ROUND(diff_minute / 60)) from rfid_zeus_oper_list where EQUIP_ID = a.EQUIP_ID and substring(regist_dt,1,4) = #{sYear}),0)) as oper_hour , FLOOR(IFNULL((select sum(diff_day) from rfid_zeus_oper_list where EQUIP_ID = a.EQUIP_ID and substring(regist_dt,1,4) = #{sYear}),0)) as oper_date , FLOOR(IFNULL((select sum(ROUND(diff_minute / 60)) from rfid_zeus_as_list where EQUIP_ID = a.EQUIP_ID and substring(regist_dt,1,4) = #{sYear}),0)) as as_hour , FLOOR(IFNULL((select sum(diff_day) from rfid_zeus_as_list where EQUIP_ID = a.EQUIP_ID and substring(regist_dt,1,4) = #{sYear}),0)) as as_date , (SELECT USER_NAME FROM RFID_ASSET WHERE ASSET_NO = a.ASSET_NO) USER_NAME FROM rfid_zeus_oper a INNER JOIN rfid_zeus b on a.EQUIP_ID = b.EQUIP_ID WHERE 1=1
AND b.REGIST_ID = #{sRegistId}
AND b.MANAGER_ID = #{sManagerId}

